const Itemtintuc = () =>{
    return(
        <div className="itemtintuc">
           <img src="https://bocatvangtravel.com.vn/Uploads/images/CamNangDuLich/ninh%20thu%E1%BA%ADn.jpg" alt="" />
           <div className="ctn_tt">
               <h3>Tiềm năng hút khách bậc nhất Việt Nam của Phan Thiết</h3>
               <p>Thống kê 8 tháng đầu năm 2019 của tỉnh Bình Thuận cho thấy, trong gần 4 triệu lượt khách đến đây có khoảng 495.000 lượt khách quốc tế (chiếm khoảng 12%). Đa số là khách Trung Quốc và các nước châu Âu đi nghỉ đông.</p>
           </div>
        </div>
    )
}

export default Itemtintuc
